  textInputStyle: {
    flex: 5,
    fontSize: 12,
    textAlign: 'center',
    borderColor: 'black',
    borderWidth: 1,
  },
  searchButtonStyle: {
    flex: 0.1,
    fontSize: 15,
    textAlign: 'center',
    color: 'blue',
  },
  searchResultsContainerStyle: {
    flex: 16,
    justifyContent: 'center',
    backgroundColor: 'red',
  },
  resultsBoxStyle: {
    justifyContent: 'center',
    flexDirection: 'row',
  },
});
